insert into exercises (description_est, description_eng, image_url, video_url) values ("Ütle selge häälega lühikesi sõnu (nt ema, karu, tänan) ja palu lapsel neid korrata","Clearly say simple words (mom, dad, yes, me) and ask your kid to repeat them","http://verbiq.com/images/NoPicture_VerbIQ_Chatbot.png","none");
insert into exercises (description_est, description_eng, image_url, video_url) values ("{{Lapse_eesnimi}} vajab mängimiseks erinevast materjalist põnevaid, kuid mitte liiga suuri mänguasju.","Give {{Lapse_eesnimi}} toys of different texture, size, and color to encourage grabbing and touching.","http://verbiq.com/images/NoPicture_VerbIQ_Chatbot.png","none");
insert into exercises (description_est, description_eng, image_url, video_url) values ("Aseta tema ette põrandale suuri, põnevaid mänguasju ja esemeid.","Put larger toys and objects on floor directly in front of {{Lapse_eesnimi}}.","http://verbiq.com/images/sketch_1.jpg","none");
insert into exercises (description_est, description_eng, image_url, video_url) values ("Räägi lapsega ja naerata kui pilgud ristuvad. Kui {{Lapse_eesnimi}} teeb häält, proovi tekitada silmsidet ja vasta naeratusega.","Talk to {{Lapse_eesnimi}} and smile when making eye-contact. If {{Lapse_eesnimi}} makes a sound, respond with a smile.","http://verbiq.com/images/sketch_2.jpg","none");
insert into exercises (description_est, description_eng, image_url, video_url) values ("Pane {{Lapse_eesnimi}} selili ning aseta aeglaselt tema kõrvale õla kõrgusele huvitav mänguasi - kord ühele, siis teisele poole. Proovi tekitada lapsel huvi ennast mänguasja poole keerata.","Place {{Lapse_eesnimi}} on the floor lying on the back. Put a toy to the left or right of {{Lapse_eesnimi}} next to the shoulder within reach to make {{Lapse_eesnimi}} reach out to either side.","http://verbiq.com/images/NoPicture_VerbIQ_Chatbot.png","none");
insert into exercises (description_est, description_eng, image_url, video_url) values ("Liigu toas ringi ja hõika erinevatest asukohtadest lapse nime, et ta keeraks pead.","Call out  {{Lapse_eesnimi}}'s name moving around in the room from different locations ","http://verbiq.com/images/sketch_3.jpg","none");
insert into exercises (description_est, description_eng, image_url, video_url) values ("Pane {{Lapse_eesnimi}} selili ning aseta peaga kohati mõni huvitav mänguasi - kord ühele, siis teisele poole.Kui ta keerab pead, liiguta mänguasja veel kõrgemale, et tal tekiks soov ennast kõhuli keerata, et eset mitte silmist kaotada.","Place {{Lapse_eesnimi}} on the floor lying on the back. Put a toy to the left or right of {{Lapse_eesnimi}} next to the head. When {{Lapse_eesnimi}} notices the toy, slowly move the toy upwards, making sure the baby tracks the toy and tries to flip to the stomack not to lose sight.","http://verbiq.com/images/NoPicture_VerbIQ_Chatbot.png","none");
