insert into exercises (description_est, description_eng, image_url, video_url) values ("Anna lapsele mängimiseks piisavalt suuri või ümaraid asju, mille tõstmiseks on vaja kasutada kahte kätt.","","http://verbiq.com/images/NoPicture_VerbIQ_Chatbot.png","none");
insert into exercises (description_est, description_eng, image_url, video_url) values ("Võta mõni mänguasi lapselt käest ära ja hoia seda haarde ulatuses, et tal tekiks soov seda tagasi nõuda või haarata.","","http://verbiq.com/images/NoPicture_VerbIQ_Chatbot.png","none");
insert into exercises (description_est, description_eng, image_url, video_url) values ("Korda koos lapsega mõnda silpi (ga, ba, ta) mida ta juba oskab. Proovi aegajalt vahelduseks mõnda teist silpi vahele ütelda.","","http://verbiq.com/images/NoPicture_VerbIQ_Chatbot.png","none");
insert into exercises (description_est, description_eng, image_url, video_url) values ("Las laps seisab, hoides sinu käest. Liiguta seejärel enda käsi ettevaatlikult pisut eemale tõmmata, et {{Lapse_eesnimi}} prooviks ise seista.","","http://verbiq.com/images/NoPicture_VerbIQ_Chatbot.png","none");
insert into exercises (description_est, description_eng, image_url, video_url) values ("Pane mõni väike (mitte alla-neelatav) asi lapsele pihku. Seejärel võta see ära ja aseta tema nähes haardeulatuses lauale/põrandale, et ta prooviks seda haarata.","","http://verbiq.com/images/NoPicture_VerbIQ_Chatbot.png","none");
insert into exercises (description_est, description_eng, image_url, video_url) values ("Näita lapsele fotosid või pilte, kus ka mõni tuttav on peal. Aita lapsel fotolt tuttav inimene leida ja ütle tema nime.","","http://verbiq.com/images/NoPicture_VerbIQ_Chatbot.png","none");
insert into exercises (description_est, description_eng, image_url, video_url) values ("Kui {{Lapse_eesnimi}} on selili, näita talle põnevat mänguasja, hoides seda lapse paremal/vasakul küljel. Proovi kas ta keerab ennast seljalt mänguasjapoolsele küljele.","","http://verbiq.com/images/NoPicture_VerbIQ_Chatbot.png","none");
